    private void jBgerarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBgerarActionPerformed
    int vet[];  
    int x, numero, p, k;
    byte encontrou;
    int quantosNumeros = Integer.parseInt(jTFnumero.getText());
        vet = new int[quantosNumeros];        
        p = 0; // primeira posição do vetor
        for (x=1; x <= quantosNumeros; x++) { 
            numero = (int) (Math.random() * 60) + 1;
            encontrou = 0;
                for (k = 0; k < p; k++) {
                    if ( numero == vet[k]) {  
                        x = x - 1;
                        encontrou = 1; 
                    }
                }    
            if (encontrou == 0) {
                vet[p] = numero;
                p = p + 1 ; //p++
            }
        }
        for (k = 0 ; k < quantosNumeros; k++) { 
        jLresultado.setText(vet[k] + " " + jLresultado.getText());
        }
    }//GEN-LAST:event_jBgerarActionPerformed

